sys_script_include.script:
function validateEmailAddress (emailStr)
{
	// Use JS coeration to grarantee emailStr is a string
	emailStr = emailStr + '';
	//Compare emailStr again the allowed syntax as epecified in the regular expression
	// If emailStr has allowed sytax, retrun true, else return false
	if(emailStr.match(/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/)){
			return true;
	}
else{

	return false;
}
}
